import React from 'react';

const TaskItem = ({ task, onDelete, onToggleComplete }) => {
    return (
        <div className={`flex justify-between items-center p-4 border-b ${task.completed ? 'bg-green-100' : 'bg-white'}`}>
            <div>
                <h3 className="text-lg font-semibold">{task.title}</h3>
                <p className="text-gray-600">{task.description}</p>
                <p className="text-sm text-gray-500">{task.dueDate} | {task.category}</p>
            </div>
            <div className="flex items-center">
                <button 
                    onClick={() => onToggleComplete(task.id)} 
                    className={`mr-2 ${task.completed ? 'text-red-500' : 'text-green-500'}`}
                >
                    {task.completed ? 'Mark Incomplete' : 'Mark Complete'}
                </button>
                <button 
                    onClick={() => onDelete(task.id)} 
                    className="text-red-500"
                >
                    Delete
                </button>
            </div>
        </div>
    );
};

export default TaskItem;
